import java.util.*;

public class Assignment2B {
    static ArrayList<String> palindromes;

    public static void main(String[] args) {
        Assignment2B tester = new Assignment2B();
        long startTime = System.currentTimeMillis();
        int i = tester.numPalindrome("dad");
        System.out.println(i);
        long endTime = System.currentTimeMillis();
        System.out.println("Time: " + (endTime - startTime) / 1000.0 + " seconds");

        startTime = System.currentTimeMillis();
        i = tester.numPalindrome("xabcba");
        System.out.println(i);
        endTime = System.currentTimeMillis();
        System.out.println("Time: " + (endTime - startTime) / 1000.0 + " seconds");

        startTime = System.currentTimeMillis();
        i = tester.numPalindrome("bbb");
        System.out.println(i);
        endTime = System.currentTimeMillis();
        System.out.println("Time: " + (endTime - startTime) / 1000.0 + " seconds");

        startTime = System.currentTimeMillis();
        i = tester.numPalindrome("");
        System.out.println(i);
        endTime = System.currentTimeMillis();
        System.out.println("Time: " + (endTime - startTime) / 1000.0 + " seconds");

    }

    public int numPalindrome(String input) {
        int numPalindromes = 0;

        for(int i = 0; i < input.length(); i++) {
            for(int k = i + 1; k <= input.length(); k++) {
                if(isPalindrome(input.substring(i, k))){
                    numPalindromes++;
                }

                //System.out.println(input.substring(i, k + i));
            }
        }
        return numPalindromes;
    }

    public static boolean isPalindrome(String input) {
        boolean pal = false;

        //Compare last and first chars until we find one that's different or we get to the middle
        for (int k = input.length() - 1, i = 0; k >= 0 && pal != true; i++, k--) {
            if (input.charAt(k) != input.charAt(i)) {
                pal = false;
                break;
            }
            pal = true;
        }
        return pal;
    }
}